    private void CBKOJURActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CBKOJURActionPerformed
if(CBKOJUR.getSelectedItem().equals("11"))
{TXTNAJUR.setText("Teknik Informatika");
TXTSPI.setText("20000000");
}

else
if(CBKOJUR.getSelectedItem().equals("12"))
{TXTNAJUR.setText("Teknik Komputer");
TXTSPI.setText("25000000");
}

else
if(CBKOJUR.getSelectedItem().equals("13"))
{TXTNAJUR.setText("Teknik Elektro");
TXTSPI.setText("15000000");
}

else
if(CBKOJUR.getSelectedItem().equals("14"))
{TXTNAJUR.setText("Teknik Sipil");
TXTSPI.setText("10000000");
}

else
if(CBKOJUR.getSelectedItem().equals("15"))
{TXTNAJUR.setText("Teknik Mesin");
TXTSPI.setText("20000000");
}

else
{TXTNAJUR.setText(null);
TXTSPI.setText(null);
}
    }//GEN-LAST:event_CBKOJURActionPerformed

