    private void BTNDAFTARActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BTNDAFTARActionPerformed
if(TXTNAMA.getText().equals("")||TXTNOPENDAFTARAN.getText().equals("")||TXTTTL.getText().equals("")||TXTALAMAT.getText().equals("")
        ||TXTNAJUR.getText().equals("")||TXTSPI.getText().equals("")||TXTUKT.getText().equals("")||TXTBIAYAPENDAFTARAN.getText().equals(""))
{
JOptionPane.showMessageDialog(null, "Data Tidak Boleh Kosong !","Kesalahan", JOptionPane.ERROR_MESSAGE);
}
else{
        JOptionPane.showMessageDialog(null,"selamat anda telah terdaftar");}
    }//GEN-LAST:event_BTNDAFTARActionPerformed

