    private void BTNHITUNGActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BTNHITUNGActionPerformed
double totalbiaya,pendaftaran,spi,ukt;
pendaftaran=Double.parseDouble(TXTBIAYAPENDAFTARAN.getText());
spi=Double.parseDouble(TXTSPI.getText());
ukt=Double.parseDouble(TXTUKT.getText());
totalbiaya=pendaftaran+spi+ukt;
TXTTOTAL.setText(String.valueOf(totalbiaya));
    }//GEN-LAST:event_BTNHITUNGActionPerformed


